var messagesList;
var form;
var messageField;
var url = '/Home/GetFortTd';
$(document).ready(function () {
    $('#OpenGame').on('click', function () {
      $('#GameContainer').load(url);
      $('#SwitchVersion').hide();
    });

    $('#restart').on('click', function () {
        location.reload();
    });

    $('#SwitchVersion').on('click', function () {
      if (url == '/Home/GetFortTd')
      {
          url = '/Home/GetUnityTd';
          $('#SwitchVersion').html("Switch to V2");
      }
      else
      {
          url = '/Home/GetFortTd';
          $('#SwitchVersion').html("Switch to V1");
      }
    });

    messagesList = $('#Chat'),
    form = $('#message-form'),
    messageField = $('#message');

    //setupWebSocket();
});

var socket;
function setupWebSocket()
{
  form.unbind('submit');
  socket = null;
  newWebSocket();
}

function newWebSocket()
{
  // Create a new WebSocket.
  //var socket = new WebSocket('ws:mattschatserver.cloudapp.net:443');
  socket = new WebSocket('ws:mattschatserver.cloudapp.net/wsChat');

  socket.binaryType = 'arraybuffer';

  // Show a connected message when the WebSocket is opened.
  socket.onopen = function (event) {
    console.log('Conn');
  };

  // Handle any errors that occur.
  socket.onerror = function (error) {
    console.log('WebSocket Error: ' + error);
  };

  // Send a message when the form is submitted.
  form.on('submit', function (e) {
    e.preventDefault();

    // Retrieve the message from the textarea.
    var message = messageField.val();

    socket.send(message);

    // Clear out the message field.
    messageField.val('');

    return false;
  });

  // Handle messages sent by the server.
  socket.onmessage = function (event) {
    beep();
    var string = event.data;
    messagesList.append('<li class="ChatLine">' + string + '</li>');
    messagesList.scrollTop(messagesList[0].scrollHeight);
  };

  socket.onclose = function () {
    setTimeout(setupWebSocket, 1000);
  };
}

function beep() {
  var snd = new Audio("data:audio/wav;base64,");
  snd.play();
}
